Method.Implementation:
If ##class(%Dictionary.ClassDefinition).%ExistsId(Name) {
	Set ClassDef = ##class(%Dictionary.ClassDefinition).%OpenId(Name, , .Status)
	Set IsNew = 0
} Else {
	; Set classversion to 25 to prevent prodedureblock and inheritance
	; getting old default values (off and right, respectively).
	Set ClassDef = ##class(%Dictionary.ClassDefinition).%New(Name, 25)
	Set IsNew = 1, Status = $$$OK
}
Quit ClassDef

If '$IsObject(ClassNameOrObj) {
	;Not an object, so a class name
	Set ClassNameOrObj = ##class(%Dictionary.CompiledClass).%OpenId(ClassNameOrObj, , .sc)
	If 'sc Quit sc
}

Set Key = ""
For  {
	Set ParameterDefinition = ClassNameOrObj.Parameters.GetNext(.Key)
	If Key = "" Quit
	If ParameterDefinition.Name = ParameterName Quit
}

Quit $$$OK

Set PropertyDefinition = "" If '$IsObject(ClassNameOrObj) { ;Not an object, so a class name Set ClassNameOrObj = ##class(%Dictionary.ClassDefinition).%OpenId(ClassNameOrObj, , .sc) If 'sc Quit sc }
Set Key = "", Found = 0 For { Set PropertyDefinition = ClassNameOrObj.Properties.GetNext(.Key) If Key = "" Quit If PropertyDefinition.Name = PropertyName Set Found = 1 Quit
} If 'Found Quit $$$ERROR($$$GeneralError, "Property "_PropertyName_" not found.")
Quit $$$OK

Set IndexLocation = ""
If '$IsObject(ClassNameOrObj) { ;Not an object, so a class name Set ClassNameOrObj = ##class(%Dictionary.ClassDefinition).%OpenId(ClassNameOrObj, , .sc) If 'sc Quit sc }
Set Key = "" For { Set StorageDef = ClassNameOrObj.Storages.GetNext(.Key) If Key = "" Quit
Set IndexLocation = StorageDef.IndexLocation Quit }
Quit $$$OK

Set MethodDefinition = "" If '$IsObject(ClassNameOrObj) { ;Not an object, so a class name Set ClassNameOrObj = ##class(%Dictionary.ClassDefinition).%OpenId(ClassNameOrObj, , .sc) If 'sc Quit sc }
Set Key = "", Found = 0 For { Set MethodDefinition = ClassNameOrObj.Methods.GetNext(.Key) If Key = "" Quit If MethodDefinition.Name = MethodName Set Found = 1 Quit
} If 'Found Quit $$$ERROR($$$GeneralError, "Method "_MethodName_" not found.")
Quit $$$OK
