Method.Implementation:
Set Changed = 0 Set NewName = Filename Set Index = 0
While ##class(%File).Exists(NewName) { Set Changed = 1 Set Index = Index + 1
;Determine filename part Set Name = ##class(%File).GetFilename(Filename) ;Don't use %File:GetDirectory as it expands the directory (with $ZU(12)) Set Dir = $Extract(Filename, 1, $Length(Filename) - $Length(Name))
If Name [ "." { ;Filename has extension: insert sequence before it Set Name = $Piece(Name, ".", 1, $Length(Name, ".") - 1)_"_"_Index_"."_$Piece(Name, ".", $Length(Name, ".")) } Else { ;No extension: just append sequence Set Name = Name_"_"_Index }
;Put directory back Set NewName = Dir_Name }
Quit NewName
