Method.Implementation:
;Assume we won't handle this action Set Action = 0, Target = "", Msg = "", Reload = 0
;Studio-generated action (as opposed to menu item) If Type = 1 Quit $$$OK
;===== Actions that are always allowed
If Name = "Source control,LaunchUserInterface" { Set Action = 3 Try { Set Host = $SYSTEM.TCPDevice.LocalAddr() } Catch (e) { Set Host = ##class(%Function).HostName() } Set Port = $Get(^%SYS("WebServer","Port"), "57772") Set Namespace = $ZUtil(5) Set Target = "http://"_Host_":"_Port_"/csp/"_Namespace_"/Strix.SCM.UI.cls" Quit $$$OK }
;===== Get project
If InternalName = "" Quit $$$OK
#dim Project As Strix.SCM.Project If '##class(Strix.SCM.Project).CheckItem(.InternalName, .Project, .AsItem, .AsStorage, .Msg) { If Msg '= "" Set Action = 6 ; MesssageBox Quit $$$OK }
;== Export item
If Name = "Source control,ExportItem", AsItem { Set Status = ##class(Strix.SCM.FileIO).ExportItem(Project, InternalName, .Filename) If 'Status Quit Status Write !,"== "_InternalName_" exported to "_Filename_"." Quit $$$OK }
;== Export class data
If Name = "Source control,ExportData", AsStorage { Set Status = ##class(Strix.SCM.FileIO).ExportClassData(Project, InternalName, .Filename) If 'Status Quit Status Write !,"== "_InternalName_" data exported to "_Filename_"." Quit $$$OK }
;== Export project
If Name = "Source control,ExportProject" { Set Action = 1 ;Yes/no/cancel Set Target = "Are you sure you want to export the entire project '"_Project.Name_"'?" Quit $$$OK }
;== Reload item
If Name = "Source control,LoadItem", AsItem { Set Filename = Project.GetExportFilename(InternalName, , , .Status) If 'Status Quit Status Set Action = 1 ;Yes/no/cancel Set Target = "Are you sure you want to reload '"_InternalName_"' from file '"_Filename_"'?" Quit $$$OK }
;== Reload data
If Name = "Source control,LoadData", AsStorage { Set StorageName = InternalName, $$$ItemType(StorageName) = "stg" Set Filename = Project.GetExportFilename(StorageName, , , .Status) If 'Status Quit Status Set Action = 1 ;Yes/no/cancel Set Target = "Are you sure you want to reload data for '"_InternalName_"' from file '"_Filename_"'?" Quit $$$OK }
;== Open folder
If Name = "Source control,OpenFolder" { Set FullPath = Project.GetExportFilename(InternalName, .Directory, .Filename) Set Action = 3 ; Run exe Set Target = ..GetExplorerCommand(Directory, Filename) Quit $$$OK }
;==
Set Action = 6 ; Messagebox Set Target = "Unimplemented menu: "_Name
Quit $$$OK

;Top level menus always enabled
If MenuName '[ "," Set Enabled = 1 Quit $$$OK

;Get display name:
Set ItemName = $Piece(MenuName, ",", 2)
Set DisplayName = $Case(ItemName,
  "ExportItem":          "Save "_InternalName_" to disk",
  "ExportData":          "Save data for "_InternalName_" to disk",
  "LoadItem":            "Reload "_InternalName_" from disk",
  "LoadData":            "Reload data for "_InternalName_" from disk",
  "ExportProject":       "Save project to disk",
  "OpenFolder":          "Open working copy folder",
  "LaunchUserInterface": "Open user interface",
  : MenuName
)

;===== Menus that don't depend on a known item

If ItemName = "LaunchUserInterface" {
	Set Enabled = 1
	Quit $$$OK
}

;===== Menus that depend on a known item

#dim Project As Strix.SCM.Project
If ##class(Strix.SCM.Project).CheckItem(.InternalName, .Project, .AsItem, .AsStorage) {
	;Default: enabled if known as item, otherwise not
	Set Enabled = $Select(AsItem: 1, 1: 0)
	
	;Exceptions:
	If ItemName = "ReloadItem" {
		If AsItem {
			Set Filename = Project.GetExportFilename(InternalName, , , .Status)
			Set Enabled = ##class(%File).Exists(Filename)
		} Else {
			Set Enabled = 0
		}
		
	} ElseIf (ItemName = "ExportData") || (ItemName = "LoadData") {
		Set Enabled = $Select(AsStorage: 1, 1: -1)
		
	}
	
} Else {
	;Unknown item in project: don't display at all
	Set Enabled = -1
	
}

Quit $$$OK

If File '= "" {
	Set Path = ##class(%File).SubDirectoryName(Directory, File)
	If '##class(%File).Exists(Path) Set File = ""
}

If File = "" {
	Set Cmd = "C:\Windows\explorer.exe /e /root,"_Directory
} Else {
	Set Cmd = "C:\Windows\explorer.exe /e,/select,"_Path
}

Quit Cmd
