Method.Implementation:
If $X Write !
Write !

Write "Tests started at "_..TestRunStart_" and completed at "_..TestRunDone_".",!
Write "Ran "_..Tests_" tests in "_..TestCases_" testcases.",!
Write "A total of "_..Asserts_" assertions were successful; "_..Skipped_" were skipped.",!
Write "There were "_..Failed_" failed assertions and "_..Errors_" errors.",!

If '..Results.Count() Quit
Write !,"Details below:",!

For i = 1:1:..Results.Count() {
	#dim Info As Strix.Testing.Info
	Set Info = ..Results.GetAt(i)
	
	Write !,"#"_i_" "_Info.ClassName_":"_Info.MethodName_":",!
	
	If Info.Type = "Fail" {
		Write Info.AssertionType_" failed: "_Info.Description,!
		
		If $ListFind($ListFromString("AssertEqual,AssertNotEqual,AssertListEqual,AssertListNotEqual"), Info.AssertionType) {
			Set Expected = Info.Expected
			Set Result = Info.Result
			ZWrite Expected
			ZWrite Result
			
		} ElseIf $ListFind($ListFromString("AssertStatusOk,AssertErrorStatus,AssertTrue,AssertFalse"), Info.AssertionType) {
			Write "Got: "_Info.Result,!
			
		} ElseIf Info.AssertionType = "AssertStatusEqual" {
			Write "Status1: "_Info.Expected,!
			Write "Status2: "_Info.Result,!
			
		}
		
		Continue
	}
	
	If Info.Type = "Skip" {
		Write "  Skipped"_$Select(Info.Description '= "": ": "_Info.Description, 1: " (no reason given)"),!
		Continue
	}
	
	If Info.Type = "Error" {
		Write "Error: "_Info.Description,!
		If Info.Status '= "", 'Info.Status Write $System.Status.GetErrorText(Info.Status),!
		Continue
	}
	
	If Info.Type = "Log" {
		Write "Message: "_Info.Description,!
		Continue
	}
	
	;Unrecognised, dump object
	Do $system.OBJ.Dump(Info)
}
