Set cd = ##class(%Dictionary.ClassDefinition).%OpenId(..#TestClassName) Do ..AssertEqual(cd.Super, "%RegisteredObject,Strix.CSV.Adaptor", "Test class has proper superclasses")
#dim pd As %Dictionary.PropertyDefinition Set pd = ##class(Strix.Testing.Support).GetProperty(cd, "a") If ..AssertNotEmpty(pd, "Property a exists.") { Do ..AssertEqual(pd.Parameters.GetAt("CsvColumnNr"), "1", "CsvColumn for property a is correct.") Do ..AssertEqual(pd.Parameters.GetAt("CsvColumnHeader"), "a", "CsvColumnHeader for property a is correct.") }
Set pd = ##class(Strix.Testing.Support).GetProperty(cd, "b") If ..AssertNotEmpty(pd, "Property b exists.") { Do ..AssertEqual(pd.Parameters.GetAt("CsvColumnNr"), "2", "CsvColumn for property b is correct.") Do ..AssertEqual(pd.Parameters.GetAt("CsvColumnHeader"), "b", "CsvColumnHeader for property b is correct.") }
Set pd = ##class(Strix.Testing.Support).GetProperty(cd, "c") If ..AssertNotEmpty(pd, "Property c exists.") { Do ..AssertEqual(pd.Parameters.GetAt("CsvColumnNr"), "3", "CsvColumn for property c is correct.") Do ..AssertEqual(pd.Parameters.GetAt("CsvColumnHeader"), "c", "CsvColumnHeader for property c is correct.") }
Do ..Stream.Rewind()
#dim Parser As Strix.CSV.Parser Set Parser = ##class(Strix.CSV.Parser).GetParser(..Stream, ",", 1) Set Parser.AdaptorClassName = ..#TestClassName If ..Assert(Parser.Next(.sc), "Parser can get record.") { Set o = Parser.GetObject() If '..Assert($IsObject(o), "An object is returned for the record.") Quit Do ..AssertEqual(o.a, "1", "Property a has correct value.") Do ..AssertEqual(o.b, "2", "Property b has correct value.") Do ..AssertEqual(o.c, "3", "Property c has correct value.") }
